import Highcharts from 'highcharts'


    
const Pchart = () => {
    Highcharts.chart('container', {
        colors: ['#01BAF2', '#71BF45', '#FAA74B', '#B37CD2'],
        chart: {
            type: 'pie'
        },
        title: {
            text: 'Brasil'
        },
        tooltip: {
            valueSuffix: '%'
        },
        plotOptions: {
            pie: {
                allowPointSelect: false,
                cursor: 'pointer',
                dataLabels: {
                    enabled: false,
                    format: '{point.name}: {y} %'
                },
                showInLegend: false
            }
        },
        credits:false,
        series: [{
            name: 'Percentage',
            colorByPoint: true,
            innerSize: '50%',
            data: [{
                name: 'Nitrogen',
                y: 78
            }, {
                name: 'Oxygen',
                y: 20.9
            }, {
                name: 'Other gases',
                y: 1.1
            }]
        }]
    });
    

