import React, {useEffect, useState} from "react";
import {Chart} from "react-google-charts";
import _, { resultado } from 'lodash';


  export const options = {
    title: "Brasil",
    pieHole: 0.4,
    is3D: false,
  };
//useEffect vai fazer a função de load, primeiro parametro é o load e o segundo o monitoramento

const Grafico1 = () => {
    
    const[chartData,setChartData] = useState([]);

    const loadData = (data) =>{
        const values = _.groupBy(data,(value)=> values.modelo);

        const resultado = _.map(values,(value,key) =>{
            return[
                key,
                _.sumBy(values[key], (v)=> v.teste)];
        });

        return[["Modelo","Testes"],...resultado]};

    

useEffect(() =>{
        const data =[ 
            {país:"Brasil", modelo:'p1', teste:23},
            {país:"Brasil", modelo:'p2', teste:12},
