            {país:"Brasil", modelo:'p3', teste:5},
            {país:"Brasil", modelo:'p4', teste:3},
            {país:"Brasil", modelo:'p1', teste:23},
            {país:"Brasil", modelo:'p2', teste:12},
            {país:"Brasil", modelo:'p3', teste:5},
            {país:"Brasil", modelo:'p4', teste:3},
        ];
        setChartData(loadData(data))
    },[]);
//--------------------dados para usar na div serviços
